<template>    <div>      <popup        v-model="showValue"        class="vux-popup-picker"        :popup-style="popupStyle">        <div class="vux-popup-picker-container">          <div class="vux-popup-picker-header" >          </div>          <picker            :data="dataList"            v-model="tempValue"          ></picker>        </div>      </popup>    </div></template><script>    import {Picker,Popup} from 'vux'    export default{        name:'SelectPopup',        data(){            return{                fixedColumns:1,                tempValue:[],                showValue:false            }        },        props: {            show2:{                type:Boolean,                default:true            },            popupStyle: {                type: Object            },            dataList:{                type:Array,                default () {                    return []                }            },            value2:{                type:Array,                default () {                    return []                }            }        },        watch:{            show2 (val) {                this.showValue = val            },            value2 (val){                this.tempValue=val            }        },        components:{            Popup,            Picker        },        method:{            onPopupHide(){            },            onPopupShow(){            },            onPickerChange(){            },        }    }</script>