<template>  <div class="showDialog" :class="{hide:!value}">    <div class="showDialogMask" @click="$emit('input',false)"></div>    <div class="dialogCont">      <!--<div class="weui-dialog__hd"><strong class="weui-dialog__title">{{title}}</strong></div>-->      <!--<div class="weui-dialog__bd">-->        <!--<slot>弹窗内容，告知当前状态、信息和解决方法，描述文字尽量控制在三行内</slot>-->      <!--</div>-->      <!--<div class="weui-dialog__ft">-->        <!--<a v-if="cancel" class="weui-dialog__btn weui-dialog__btn_default" @click="onCancel">{{cancelVal}}</a>-->        <!--<a v-if="confirm" class="weui-dialog__btn weui-dialog__btn_default" @click="onConfirm">{{confirmVal}}</a>-->      <!--</div>-->    </div>  </div></template><script>    export default {        name: 'dialog',        props: {            value: {                type: Boolean,                default: false            },            confirm: {                type: Boolean,                default: false            },            cancel: {                type: Boolean,                default: false            },            title: {                default: null            },            confirmVal: {                type: String,                default: '是'            },            cancelVal: {                type: String,                default: '否'            },        },        methods: {            onConfirm(){                this.$emit('onConfirm')                this.$emit('input', false)            },            onCancel(){                this.$emit('onCancel')                this.$emit('input', false)            }        }    }</script><style lang="less" scoped>  .showDialog {    width: 100%;    height: 100%;    .showDialogMask {      position: fixed;      z-index: 1000;      top: 0;      right: 0;      left: 0;      bottom: 0;      background: rgba(0, 0, 0, .6);    }    .dialogCont{      position: fixed;      z-index: 5000;      width: 80%;      max-width: 300px;      top: 50%;      left: 50%;      transform: translate(-50%, -50%);      background-color: @weuiDialogBackgroundColor;      text-align: center;      border-radius: 3px;      overflow: hidden;    }  }</style>