<template>  <div class="hotelBasicInfo">    <group gutter="0">      <x-input title="酒店名字" v-model="hotelName" placeholder='请输入酒店名称' placeholder-align="right" text-align="right" :readonly="!btnFlag" ></x-input>      <!--<selector title="地区" v-model="area":options="arealist" direction="rtl" placeholder='请选择选择地区' placeholder-align="right" text-align="right" :readonly="!btnFlag"></selector>-->      <x-input title="地区" v-model="area" placeholder='请输入地区' placeholder-align="right" text-align="right" :readonly="!btnFlag"></x-input>      <x-input title="地址" v-model="address" placeholder='请输入地址' placeholder-align="right" text-align="right" :readonly="!btnFlag"></x-input>      <x-input title="电话" v-model="tel" placeholder='请输入前台电话' placeholder-align="right" text-align="right" :readonly="!btnFlag"></x-input>      <x-input title="旅业ID" v-model="lvId" placeholder='请输入旅业ID' placeholder-align="right" text-align="right" :readonly="!btnFlag"></x-input>      <x-switch title="无证入住" v-model="undocumentedCheck" ></x-switch>    </group>    <div class="bottonGroup">      <span class="rightBtn" @click="conFirmOrEdit">{{btnFlag?'确定':'编辑'}}</span>    </div>  </div></template><script>    import { XInput,XSwitch,Selector,Group } from 'vux'    import { mapActions,mapState,mapMutations }from 'vuex'    export default {        components: {            XInput,            XSwitch,            Selector,            Group        },        data(){            return{                // arealist:[{key: 'gd', value: '广东'}, {key: 'gx', value: '广西'}],                // btnFlag:true,                hotelName:"" ,//酒店名称                area:"", //    地区                address:"", // 地址                tel:"", //  联系电话                lvId:"", // 旅业ID                undocumentedCheck:false, // 无证入住,                btnFlag:true            }        },        computed:{            ...mapState([                'searchHotelId'            ])        },        watch: {            searchHotelId (val) {                if (val == '') {                    this.btnFlag = true                } else {                    this.btnFlag = false                }                if (val !== '') {                    this.modifyHotelInfo ({                        hotelId: this.searchHotelId,                        onsuccess: body => {                            this.hotelName = body.data.hotelName ,//酒店名称                                this.area = body.data.area, //    地区                                this.address = body.data.address, // 地址                                this.tel = body.data.tel, //  联系电话                                this.lvId = body.data.lvyeId, // 旅业ID                                this.undocumentedCheck = body.data.undocumentedCheck// 无证入住,                        }                    });                }                console.log ('btnFlag:', this.btnFlag)            },        },        methods:{            ...mapActions([                'addHotelInfo',                'updateHotelInfo',                'modifyHotelInfo',                'goto'            ]),            ...mapMutations([                'SEARCHHOTELID'            ]),            conFirmOrEdit(){                if(!this.btnFlag){                    this.btnFlag=!this.btnFlag                }else {                    if(this.searchHotelId!==''){                        this.updateHotelInfo({                            data:{                                "hotelId":this.searchHotelId,                                "hotelName":this.hotelName, //酒店名称                                "area":this.area, //    地区                                "address":this.address, // 地址                                "tel":this.tel, //  联系电话                                "lvyeId":this.lvId, // 旅业ID                                "undocumentedCheck":this.undocumentedCheck,                            },                            onsuccess:body=>{                                this.SEARCHHOTELID(this.searchHotelId)                                this.goto('/onlineTool/onlineDetail')                            }                        })                    }else {                        this.addHotelInfo({                            data:{                                "hotelName":this.hotelName, //酒店名称                                "area":this.area, //    地区                                "address":this.address, // 地址                                "tel":this.tel, //  联系电话                                "lvyeId":this.lvId, // 旅业ID                                "undocumentedCheck":this.undocumentedCheck,                            },                            onsuccess:body => {                                this.SEARCHHOTELID(body.data)                                this.goto('/onlineTool/onlineDetail')                            }                        })                    }                }            }        },        mounted() {            if (this.searchHotelId){                this.modifyHotelInfo({                    hotelId:this.searchHotelId,                    onsuccess:body=>{                        console.log()                        this.hotelName=body.data.hotelName ,//酒店名称                            this.area=body.data.area, //    地区                            this.address=body.data.address, // 地址                            this.tel=body.data.tel, //  联系电话                            this.lvId=body.data.lvyeId, // 旅业ID                            this.undocumentedCheck=body.data.undocumentedCheck// 无证入住,                    }                });            };            if (this.searchHotelId == '') {                this.btnFlag = true            } else {                this.btnFlag = false            }            console.log('btnFlag:',this.btnFlag)        }    }</script><style lang="less" scoped>  @import './index.less';</style>