<template>  <div class="onlineList">    <article >      <group>        <cell v-for="(item,index) in list" :key="index" :title="item.hotelName" link='/onlineTool/onlineDetail'  @click.native="gotoDetail(item.hotelId)" :inline-desc="item.area+' '+datetimeparse(item.update_time,'YYMMDD')+' 上线'"> </cell>      </group>    </article>    <p class="addHotel" @click="addHotel">+ 添加酒店</p>  </div></template><script> import {mapActions,mapState,mapMutations} from 'vuex' import {Group,Cell} from 'vux' export default{     data(){         return{             list:[{                 hotelName:'俊园酒店',                 area:'苏州',                 update_time:1524552515,                 hotelId:5678765             },                 {                     hotelName:'轩尼斯酒店',                     area:'苏州',                     update_time:1524552515,                     hotelId:3463663                 }             ]             // list:[]         }     },     computed:{         ...mapState([             'searchHotelId'         ])     },     components:{         Group,         Cell     },     methods:{         ...mapActions([             'goto',             'searchOnlineHotleList'         ]),         ...mapMutations([             'SEARCHHOTELID'         ]),         addHotel(){             this.SEARCHHOTELID('');             this.goto('/onlineTool/hotelBasicInfo');         },         gotoDetail(hotelId){             this.SEARCHHOTELID(hotelId);         }     },     mounted(){         this.searchOnlineHotleList({             onsuccess:body => {                 this.list=(body.data?[body.data]:[...this.list]);                 console.log(this.list)             }         })     } }</script><style lang="less" scoped>  @import './index.less';</style>