<template>  <div class="onlineList">    <div class="search">      <span class="searchInput">      <span class="iconfont icon-sousuo"></span>        <input type='text' @onfocus="placeholder=''" placeholder="搜索" v-model="hotelName" autocomplete="off" autocorrect="off" spellcheck="false" autocapitalize="off">      </span>      <span class="confirm" @click="search">确定</span>    </div>    <article >      <group gutter="0">        <cell v-for="(item,index) in list" :key="index" :title="item.hotelName" is-link  @click.native="gotoDetail(item.hotelId)" :inline-desc="item.area+' '+datetimeparse(item.createtime,'YYMMDD')+' 上线'"> </cell>      </group>    </article>    <p class="addHotel" @click="addHotel">+ 添加酒店</p>  </div></template><script> import {mapActions,mapState,mapMutations} from 'vuex' import {Group,Cell} from 'vux' export default{     data(){         return{             hotelName:'',             list:[]         }     },     computed:{         ...mapState([             'searchHotelId'         ])     },     components:{         Group,         Cell     },     methods:{         ...mapActions([             'goto',             'searchOnlineHotleList',             'searchHotel'         ]),         ...mapMutations([             'SEARCHHOTELID'         ]),         addHotel(){             this.SEARCHHOTELID('');             this.goto('/onlineTool/hotelBasicInfo');         },         gotoDetail(hotelId){             this.goto('/onlineTool/onlineDetail')             this.SEARCHHOTELID(hotelId);         },         search(){             this.searchHotel({                 hotelName:this.hotelName,                 onsuccess:body => {                     this.list=body.data                 }             })         }     },     created(){         this.searchOnlineHotleList({             onsuccess:body => {                 this.list=(body.data?[...body.data]:[]);                 console.log(this.list)             }         })     } }</script><style lang="less" scoped>  @import './index.less';</style>