<template>  <div class="hotelBasicInfo">    <group>      <selector title="设备类型" v-model="deviceType":options="arealist" direction="rtl" placeholder='请选择设备类型' placeholder-align="right" text-align="right"></selector>      <x-input title="设备名称" v-model="deviceName" placeholder='请输入设备名称' placeholder-align="right" text-align="right"></x-input>      <cell title="设备序列号" v-model="macAddress" placeholder-align="right" text-align="right" @click.native="scanQRcode">        <i slot="default"  class="iconfont icon-erweima"></i>      </cell>      <x-input title="电脑MAC地址" v-model="deviceSn" placeholder='接受信息电脑MAC地址' placeholder-align="right" text-align="right"></x-input>    </group>    <p class="bottonGroup" click="conFirm">确定</p>  </div></template><script>    import wx from 'weixin-js-sdk'    import { XInput,XSwitch,Selector,Cell} from 'vux'    import { mapActions} from 'vuex'    export default {        components: {            XInput,            XSwitch,            Selector,            Cell        },        data(){            return{                arealist:[{key: 'gd', value: '人证通'}, {key: 'gx', value: '底座'}],                deviceType:'',                deviceName:'',                deviceSn:'',                macAddress:''            }        },        methods:{            ...mapActions([                'addDevice'            ]),            back(){            },            conFirm(){            },            scanQRcode(){                wx.scanQRCode({                    desc: 'scanQRCode desc',                    needResult: 1, // 默认为0，扫描结果由企业微信处理，1则直接返回扫描结果，                    scanType: ["qrCode", "barCode"], // 可以指定扫二维码还是一维码，默认二者都有["qrCode", "barCode"]                    success: function(res) {                        this.deviceSn=res                        console.log('res:',res)                        // 回调                    },                    error: function(res) {                        console.log('errorRes:',res)                        if (res.errMsg.indexOf('function_not_exist') > 0) {                            alert('版本过低请升级')                        }                    }                });            },        }    }</script><style lang="less" scoped>  @import './index.less';  .icon-erweima{    color: #4660c7;  }</style>