<template>  <div>    <div class="orderTitle">      <span>同步</span>      <span>        <x-input  placeholder="输入手机号或姓名拼音快速索引" v-model="searchOrder">        <i slot="label" style="padding-right:10px;display:block;" class="iconfont icon-sousuo" width="24" height="24"></i>        </x-input>      </span>    </div>    <div v-for="(item,index) in renderList" class="wrap" @click="chooseOrder(item)">      <p><span class="item">订单号：</span><span>{{item.phone_number}}</span><span style="float:right;color:#2c5fc3 "> 预付</span></p>      <div class="line"></div>      <p><span class="item">预定人：</span><span>{{item.name}}</span></p>      <p><span class="item">房型：</span><span>{{item.order_num}}</span></p>      <p><span class="item">预付款：</span><span>{{item.order_num}}</span></p>      <p><span class="item">入住：</span><span>{{item.order_num}}</span><span style="margin-left: 1rem;color: #959595">离店：</span><span>{{item.order_num}}</span></p>      <div class="line"></div>      <p><span class="item">备注：</span><span>{{item.order_num}}</span></p>    </div>  </div></template><script>    import {mapActions,mapState,mapMutations} from 'vuex'    import {Cell,XInput} from 'vux'    export default {        components:{            XInput        },        data(){            return{                list:[{order_num:5575658,phone_number:44241212,name:'郑斯洁'},{order_num:5575658,phone_number:173632824,name:'汪大婷'},{order_num:5575658,phone_number:183469343,name:'孙书jing'}],                orderList:[],                searchOrder:'',                resultList:[]            }        },        methods:{            ...mapActions([                'goto',                'replaceto',                'getAllOrderList'            ]),            ...mapMutations([                'CHECKORDERITEM'            ]),            //选择一个订单            chooseOrder(item){                let list=[];                list.push(item);                this.CHECKORDERITEM(list)                this.goto(-1);            }        },        computed:{            ...mapState([                'route',                'Interface'            ]),            renderList(){                console.log('this.resultList:',this.resultList)               return this.resultList;            },        },        watch:{            searchOrder(val,old) {                console.log(old)                console.log(val)                if (val=='') {                    this.resultList =this.list;                    // this.isErrorNumber = false;                }                // if (!this.canSearch) return;                if (val && val.split('').some(i => !/^[\u4e00-\u9fa5_a-zA-Z0-9]+$/.test(i))) {//验证特殊字符                    // console.log(333333)                    this.searchOrder = old;                    // this.resultList = [];                }                if (this.list.length > 0 && val) {                    this.resultList = [];                    this.resultList = this.list.filter(item=> item.phone_number.toString().indexOf(val) > -1||item.name.indexOf(val) > -1)                    // if (this.resultList.length === 0) {                    //     this.isErrorNumber = true;                    // }                    }                }        },        mounted(){            this.resultList=this.list;            // this.getAllOrderList({            //     data:{            //         precheckin_status:'',            //         like_owner: '',            //         pms_room_type_id: '',            //     },            //     onsuccess:body=>{            //         this.orderList=body.data;            //         this.resultList=this.orderList;            //     }            // })        }    }</script><style lang="less" scoped>  @import 'orderSearch.less';</style>