<template>  <div class="detailWrap">    <p class="cell cell1">      <span>抓拍时间</span>      <span>{{datetimeparse(suspiciousDetail.filming_time,'YYYYMMDDhhmmss')}}</span>    </p>    <div class="cell cell2">      <p>        <span>抓拍人脸图</span>      </p>      <div class="compareItem">        <div class="item compare1">          <img :src="suspiciousDetail.facial_pic" alt="无人脸图">        </div>      </div>    </div>    <div class="cell cell3">      <p>抓拍实景图</p>      <div class="sceneBox">        <img :src="suspiciousDetail.origin_pic" alt="无实景图"  class="sceneImg">      </div>      <p>公安敏感人群比对结果</p>    </div>    <div class="button-group"  >      <x-button  v-if="flag!='READ'"value="已确认,移除到已处理列表" @onClick="handelBtn" ></x-button>      <span v-else style="color:#22AAFF;margin-left: -3rem">已确认</span>    </div>  </div></template><script>    import {mapState, mapGetters, mapActions, mapMutations} from 'vuex';    module.exports= {        data(){            return{                suspiciousDetail:{illegal_guest_id:1,similarity:"50%",filming_time:"12:20"},                flag:false            }        },        computed: {            ...mapState([                'route',            ]),            suspiciousId(){                return this.$route.params.id            }        },        watch:{            // suspiciousId(val){            //     this.getDetail()            // }        },        methods:{            ...mapActions([               'getSuspiciousDetail',               'hasChecked',               'goto',               'replaceto'            ]),            getDetail(){                console.log('suspiciousId:',this.suspiciousId)                console.log('详情刷新')                this.getSuspiciousDetail({                    illegalGuestId:this.suspiciousId,                    onsuccess:body=>{                        this.suspiciousDetail=body.data;                        this.flag=this.suspiciousDetail.status                    }                });            },            handelBtn(){                this.hasChecked({                    illegalGuestId:this.suspiciousId,                    onsuccess:body=>{                        this.flag='READ'                        this.goto(-1)                    }                })            }        },        activated(){            this.getDetail();        },        created(){            this.getDetail();        }    }</script><style lang="less" scoped>  .detailWrap{    margin-top: 1rem;    padding-left: 1rem;    /*padding-right: -2rem;*/    background: #FFFFFF;    width: 95.5%;    /*height: 100%;*/    font-size: 14px;    img{      display: inline-block;      width: 100%;      height: 100%;    }    .cell {      border-bottom: 1px solid #EEEEEE;      span:nth-child(2){        float: right;        margin-right: 2rem;      }      &:last-child{        border-bottom: none;      }    }    .cell1{      height: 2.2rem;      line-height: 2.2rem;    }    .cell2{      padding: 1rem 0;      height: 11rem;      position: relative;      padding-right: 1rem;      p{        text-align: center;      }    }    .cell3{      padding-top: 1rem;      padding-bottom: 1rem;      /*height: 20rem;*/      .sceneBox{        /*height: 13rem;*/        /*background: #dadada;*/        margin-top: 1rem;        margin-right: 1rem;        .sceneImg{          display: block;          @media screen {            width: 100%;            @media(min-width:510px){              margin: auto;              width: 40%;              height: 40%;            }          }        }      }      p:nth-child(3){        color: #FF0000;        text-align: center;        margin-top: 1rem;        margin-right: 2rem;      }    }    .compareItem{      margin-top: 1rem;      .item{        width: 6rem;        height: 8rem;        margin: auto;      }      .compare1{        background: #dadada;      }      .compare2{        clear: right;        position: absolute;        /*width: 5rem;*/        /*height: 2rem;*/        left: 50%;        top: 50%;        margin-left: -2.3rem;      }      .compare3{        float:right ;        background: #dadada;        margin-right: 2rem;      }    }    .button-group{      width: 70%;      margin:auto;      .weui-btn_default{        color: #ffffff;      }    }  }</style>